import { useState, useEffect } from 'react'

const useFetch = (url) => {
    const [ data, setData ] = useState([])

    useEffect(() => {
        const fetchResource = async () => {
            let res = await fetch(url)
            let data = await res.json()
            setData(data)
        }
        fetchResource()
    }, [url])

    return { data }
}


export default useFetch
